CREATE TABLE PROVENANCE (id INTEGER NOT NULL AUTO_INCREMENT, datasetId VARCHAR(255), datasetTitle VARCHAR(255), submitterId VARCHAR(255), sipId VARCHAR(255), wfInstanceId VARCHAR(255), date timestamp, status VARCHAR(255), PRIMARY KEY ( id ));
CREATE TABLE EVENT (eventId INTEGER NOT NULL AUTO_INCREMENT, sipId VARCHAR(255), eventType VARCHAR(255), percentage INTEGER, date timestamp,  status VARCHAR(255), detail VARCHAR(1023), PRIMARY KEY ( eventId ));
CREATE TABLE roles (ROLEID INTEGER NOT NULL AUTO_INCREMENT, ROLE VARCHAR(255), PRIMARY KEY ( ROLEID ));
CREATE TABLE users (FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), EMAILADDRESS VARCHAR(255), PASSWORD VARCHAR(255), REGSTATUS VARCHAR(255), REGISTRYID VARCHAR(255), ROLEID INTEGER, VIVOID VARCHAR(1023), PRIMARY KEY ( EMAILADDRESS ),FOREIGN KEY (ROLEID) REFERENCES roles(ROLEID));